    private void calculaParejas(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_calculaParejas
        // TODO add your handling code here:
        /// aqui voy a hacer la magia
        int meses  = Integer.parseInt( txtMeses.getText());
        long cantidadconejos = calculaConejos(meses -1); 
        txtResultado.setText("" + cantidadconejos);
    }//GEN-LAST:event_calculaParejas

    ///metodo recursivo 
    
    private static long calculaConejos(long m){
        if (m<=1) return 1;  
        else return calculaConejos(m-1) + calculaConejos(m-2);  
    }
    
    private boolean compara(int a, int b){
        boolean resultado = (a == b); 
        return resultado; 
    }
    
